        List<DataItem> items = CborDecoder.decode(data);
        CryptoAccount cryptoAccount = CryptoAccount.fromCbor(items.get(0));
        Assert.assertEquals("37b5eed4", TestUtils.bytesToHex(cryptoAccount.getMasterFingerprint()));

